(function(){'use strict';function initSmoothScrolling(){const anchorLinks=document.querySelectorAll('a[href^="#"]');anchorLinks.forEach(link=>{link.addEventListener('click',function(e){const href=this.getAttribute('href');if(!href||href==='#'){return}const targetId=href.substring(1);const targetElement=document.getElementById(targetId);if(targetElement){e.preventDefault();targetElement.scrollIntoView({behavior:'smooth',block:'start'});if(history.pushState){history.pushState(null,null,href)}}})})}if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',initSmoothScrolling)}else{initSmoothScrolling()}})();(function(){'use strict';const PERF_CONFIG={PERSONAL_INFO_THRESHOLD:2000,FCP_THRESHOLD:1500,DEBUG_MODE:true};function logMetric(name,value,threshold,unit='ms'){if(!PERF_CONFIG.DEBUG_MODE)return;const status=threshold&&value>threshold?'⚠️ EXCEEDS':'✓ OK';const thresholdInfo=threshold?` (threshold: ${threshold}${unit})`:'';console.log(`[Performance] ${name}: ${value.toFixed(2)}${unit}${thresholdInfo} ${status}`)}function measureFCP(){if('PerformanceObserver'in window){try{const observer=new PerformanceObserver((list)=>{for(const entry of list.getEntries()){if(entry.name==='first-contentful-paint'){logMetric('First Contentful Paint (FCP)',entry.startTime,PERF_CONFIG.FCP_THRESHOLD);observer.disconnect()}}});observer.observe({entryTypes:['paint']})}catch(e){console.warn('[Performance] PerformanceObserver not supported:',e)}}else{console.warn('[Performance] PerformanceObserver API not available')}}function measurePersonalInfoLoadTime(){const startTime=performance.timing.navigationStart;const nameElement=document.querySelector('h1');const titleElement=document.querySelector('header p');const photoElement=document.querySelector('header img');if(nameElement&&titleElement&&photoElement){const currentTime=performance.now();const loadTime=currentTime;logMetric('Personal Information Load Time',loadTime,PERF_CONFIG.PERSONAL_INFO_THRESHOLD);return loadTime}return null}function measurePageLoadTime(){if(performance.timing){const loadTime=performance.timing.loadEventEnd-performance.timing.navigationStart;logMetric('Total Page Load Time',loadTime,null);return loadTime}return null}function measureDOMContentLoaded(){if(performance.timing){const domTime=performance.timing.domContentLoadedEventEnd-performance.timing.navigationStart;logMetric('DOM Content Loaded',domTime,null);return domTime}return null}function getPerformanceMetrics(){const metrics={personalInfoLoadTime:null,fcpTime:null,domContentLoadedTime:null,totalLoadTime:null,timestamp:new Date().toISOString()};metrics.personalInfoLoadTime=measurePersonalInfoLoadTime();if(performance.getEntriesByType){const paintEntries=performance.getEntriesByType('paint');const fcpEntry=paintEntries.find(entry=>entry.name==='first-contentful-paint');if(fcpEntry){metrics.fcpTime=fcpEntry.startTime}}metrics.domContentLoadedTime=measureDOMContentLoaded();metrics.totalLoadTime=measurePageLoadTime();return metrics}function initPerformanceMonitoring(){measureFCP();measurePersonalInfoLoadTime();window.addEventListener('load',function(){setTimeout(function(){measureDOMContentLoaded();measurePageLoadTime();if(PERF_CONFIG.DEBUG_MODE){console.log('[Performance] All metrics:',getPerformanceMetrics())}},0)})}window.CVPerformance={getMetrics:getPerformanceMetrics,config:PERF_CONFIG};if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',initPerformanceMonitoring)}else{initPerformanceMonitoring()}})();